(()=>{"use strict";const t=new class{constructor(t,n){this.title=t,this.logo=n}}("LunchApp","../img/plate.svg"),n=new class{constructor(t,n){this.copyright=t,this.credits=n}}("Copyright 2021","Developed by Sarah Falter"),e=new class{constructor(t){this.body=t}randomizeLunches(){let t=[["taco","../img/plate.svg"],["taco2","../img/taco2.png"],["taco3","../img/taco3.png"],["taco4","../img/taco4.png"],["taco5","../img/taco5.png"],["taco6","../img/taco6.png"],["taco7","../img/taco7.png"],["taco8","../img/taco8.png"]];return t[Math.floor(Math.random()*t.length)]}showResults(){document.getElementById("content").innerHTML=this.randomizeLunches()[0],document.getElementById("food-img").src=this.randomizeLunches()[1]}addBtn(){document.getElementById("clickme").addEventListener("click",(()=>{let t=this.randomizeLunches();document.getElementById("content").innerHTML=t[0],document.getElementById("food-img").src=t[1]}))}}("anything");new class{constructor(){this.renderTemplate(),e.addBtn(),e.showResults()}renderTemplate(){const e=`\n      <header>\n        <p>${t.title}</p>\n        <img src="${t.logo}" alt="" title="">\n      </header>\n      <main>\n      <section>\n        <h1>What's For Lunch?</h1>\n        <button id="clickme"></button>\n      </section>\n      <section id="content">\n\n      </section>\n      <img id="food-img" src="" alt="" title="">\n      </main>\n      <footer>\n        <p>${n.copyright}</p>\n        <p>${n.credits}</p>\n      </footer>\n    `;document.body.innerHTML=e}}})();